mapper.update:
update ac_activity
set detail_url = #{newUrl}
where id = #{activityId}

update ac_activity
set joined_num = #{number}
where id = #{activityId}

update ac_activity
set comment_num = #{number}
where id = #{activityId}

update ac_activity
set tickets_num = #{number}
where id = #{activityId}

update ac_activity
set collect_num = #{number}
where id = #{activityId}

mapper.select:
select count(*)
from ac_activity
where organizer_id = #{userId}

select *
from ac_activity
where organizer_id = #{userId}
order by created_time desc
limit #{offset},#{pageSize}
